import React from 'react';

export default function Form({ capital, taxa, periodo, onForm }) {
  const handleInputChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;

    if (name === 'capital') {
      onForm(value, taxa, periodo);
    }

    if (name === 'taxa') {
      onForm(capital, value, periodo);
    }

    if (name === 'periodo') {
      onForm(capital, taxa, value);
    }
  };

  return (
    <div>
      <div className="container">
        <div className="row">
          <h3 className="center padding">
            <i className="small material-icons">polymer</i>
            &nbsp;Calculadora de Juros Compostos
          </h3>
        </div>

        <div className="row">
          <div className="input-field col s4">
            <span>Capital Inicial:</span>
            <input
              type="number"
              name="capital"
              min="0"
              value={capital}
              onChange={handleInputChange}
            />
          </div>

          <div className="input-field col s4">
            <span>Taxa de Juros Mensal:</span>
            <input
              type="number"
              name="taxa"
              min="-12"
              max="12"
              value={taxa}
              onChange={handleInputChange}
              step="0.1"
            />
          </div>

          <div className="input-field col s4">
            <span>Per√≠odo (mensal):</span>
            <input
              type="number"
              name="periodo"
              min="1"
              max="36"
              sleep="2"
              value={periodo}
              onChange={handleInputChange}
              step="1"
            />
          </div>
        </div>
      </div>
    </div>
  );
}
